sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {TransformApiHelpers} params.helpers
 */
function evaluateProperty({
    api,
    helpers
}) {
    var jsonObj = api.data.aggregation_query_1.output;
    console.log('log 1'+ JSON.stringify(jsonObj));
    var numOfLoc = jsonObj.data.GlideAggregate_Query.length;
    var jsonVar = "[";
    for (var i = 0; i < numOfLoc; i++) {
        jsonVar = jsonVar + '{"id":"' + jsonObj.data.GlideAggregate_Query[i].groupBy[0].value + '","label":"' + jsonObj.data.GlideAggregate_Query[i].groupBy[0].displayValue + '"}';
        if (i < (numOfLoc - 1)) {
            jsonVar = jsonVar + ",";
        }
    }
    jsonVar = jsonVar + ']';
    console.log('log 2'+ jsonVar);
    return jsonVar;
}
